insert into Lingua (id, nome, descrizione) values(0, 'Lingua cinese', '<div style="text-align: justify;">Il cinese mandarino è la lingua principalmente parlata in Cina ed ha 4 toni. Questo sito tratterà principalmente il <strong>cinese semplificato</strong>.<p>La differenza tra il cinese tradizionale ed il cinese semplificato risiede nella scrittura, infatti nel cinese tradizionale i caratteri risultano più complessi ed hanno più tratti.</p><ul style="text-align: center; list-style: none; gap: 1.5rem;">Es.<c class="lxgw-wenkai-tc-regular" style="font-size: 3rem;"><li>媽 -> 妈</li><li>語 -> 语</li><li>環 -> 环</li><li>區 -> 区</li><li>體 -> 体</li><li>長 -> 长</li></ul></c><p>Non vi sono invece differenze sulla pronuncia.</p></div>');